import requests
import json
import bs4

#Building Database Structure
Stores = {}
Features = {"Reserve":"icon_reserve.jpg","Fizzo":"icon_fizzio.jpg","Drive-Thru":"Drive-Thru","WiFi":"icon_wifi.jpg",}
link = "http://www.starbucks.com.tw/stores/storesearch.jspx"
CityCodes = {"14":"基隆市","11":"台北市","1":"新北市","4":"桃園市","2":"新竹縣","21":"新竹市","23":"苗栗縣","7":"台中市","15":"彰化縣","3":"雲林縣","12":"嘉義縣","9":"嘉義市","26":"台南市","24":"高雄市","13":"屏東縣","16":"台東縣","20":"花蓮縣","17":"宜蘭縣","5":"南投縣","19":"澎湖縣","25":"金門縣","10":"連江縣","22":"南海諸島","29":"其他"}

#Loop Cities
for each in CityCodes:
    Form = { #Area-Based Search
        "AJAXREQUEST":"sbForm:j_id_jsp_1422024916_2",
        "selCity":each,
        "selRegion":"ALL",
        "sbForm:j_id_jsp_1422024916_7":"sbForm:j_id_jsp_1422024916_7",
        "javax.faces.ViewState":""
    }
    Page = requests.post(link, data=Form)
    Soup = bs4.BeautifulSoup(Page.text, "lxml")
    
    #Skipped if no results
    if len(Soup.find_all("li")) == 0:
        print("Skipped:", CityCodes[each])
        continue
    
    print("Scaning:", CityCodes[each])
    
    #Saving each Store
    for storeNum in range(len(Soup.find_all("li"))):
        #Fetch Basic Infos
        Store = {
            "ID":Soup.find_all("li")[storeNum]["id"],
            "Name":Soup.find_all("li")[storeNum].h3.string.replace("\xa0"," "),
            "Tel":Soup.select(".tel")[storeNum].string,
            "Address":Soup.select(".address")[storeNum].string,
            "Open Hours":Soup.select(".hours")[storeNum].string,
        }
        
        #Fetch Special Features
        for feature in Features:
            if Features[feature] in str(Soup.find_all("li")[storeNum]):
                Store[feature]=True
            else:
                Store[feature]=False
        
        #Append to List
        Stores[Soup.find_all("li")[storeNum].h3.string.replace("\xa0"," ")] = Store
        print("Saved:", Soup.find_all("li")[storeNum].h3.string.replace("\xa0"," "))

#Export
with open("Starbucks Stores.json", "w") as f:
    json.dump(Stores, f)